import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import fetchBlogsData from "../../redux/thunk/fetchBlogs";

// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

// import required modules
import { Pagination } from "swiper";

const Home = () => {
  const blogs = useSelector((state) => state.blog.blogs);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchBlogsData());
  }, []);

  return (
    <section className="container px-16 lg:px-32 my-12">
      <div>
        <h2 className="text-3xl font-bold text-center">Trending</h2>
      </div>
      <>
        <Swiper
          pagination={{
            dynamicBullets: true,
          }}
          modules={[Pagination]}
          className="mySwiper"
        >
          {blogs?.map((blog) => (
            <SwiperSlide>
              <div className="card lg:card-side bg-base-100 shadow-xl mt-12 ">
                <figure className="basis-1/2">
                  <img
                    src={blog?.blog_img}
                    alt="Album"
                    className="object-fill"
                  />
                </figure>
                <div className="card-body basis-1/2">
                  <div className="flex ">
                    <p className="font-bold">Business, Travel</p>
                    <p>{blog?.publish_date}</p>
                  </div>
                  <h2 className="card-title text-3xl font-bold my-3">{blog?.blog_title}</h2>
                  <p className="text-[#ADADAD]">{blog?.desc.slice(0, 200) + "..."}</p>
                  <div className="flex">
                    <div>
                    <img src={blog?.author_img} className="rounded-full h-12 w-12  object-cover"/>
                    </div>
                    <div>
                      <h3 className="font-bold text-md">{blog?.author_name}</h3>
                      <p className="text-[#ADADAD] ">Author Designation</p>
                    </div>
                  </div>
                </div>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      </>
    </section>
  );
};

export default Home;
