import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import fetchBlogsData from "../../redux/thunk/fetchBlogs";

const Blogs = () => {
  const blogs = useSelector((state) => state.blog.blogs);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchBlogsData());
  }, []);

  return (
    <section className="container px-16 lg:px-24 my-12">
      <div>
        <h2 className="text-3xl font-bold text-center">Blogs</h2>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
        {blogs?.map((blog) => <div className="card card-compact w-96 bg-base-100 shadow-xl">
            <figure>
              <img
                src={blog?.blog_img}
                alt="Shoes"
                className="h-[30%]"
              />
            </figure>
            <div className="card-body">
              <h2 className="card-title">{blog?.blog_title}</h2>
              <p>If a dog chews shoes whose shoes does he choose?</p>
              <div className="card-actions justify-end">
                <button className="btn btn-primary">Buy Now</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Blogs;
