import React, { useEffect } from 'react'
import {useDispatch, useSelector} from "react-redux"
import fetchBlogsData from '../../redux/thunk/fetchBlogs'

// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";

// import required modules
import { Pagination } from "swiper";

const Home = () => {
  const blogs = useSelector(state => state.blog.blogs)
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(fetchBlogsData())
  },[])

  return (
    <section className='container px-16 lg:px-32 pt-20'>
        <div>
            <h2 className='text-3xl font-bold text-center'>Trending</h2>
        </div>
        <>
        <Swiper
        pagination={{
          dynamicBullets: true,
        }}
        modules={[Pagination]}
        className="mySwiper"
      >
        {
        blogs?.map(blog =>  <SwiperSlide>
          <div className="card lg:card-side bg-base-100 shadow-xl">
  <figure><img src="/images/stock/photo-1494232410401-ad00d5433cfa.jpg" alt="Album"/></figure>
  <div className="card-body">
    <h2 className="card-title">New album is released!</h2>
    <p>Click the button to listen on Spotiwhy app.</p>
    <div className="card-actions justify-end">
      <button className="btn btn-primary">Listen</button>
    </div>
  </div>
</div>
        </SwiperSlide>)
        }
      </Swiper>
    </>
    </section>
  )
}

export default Home
